mapper.insert:
INSERT INTO TABLE_A
(
	EVENT_SCHEDULE_IMAGE_ID,
	USER_ID
)
VALUES
(
	#{event_schedule_image_id},
	#{user_id}
)

mapper.delete:
DELETE FROM TABLE_A
WHERE

		EVENT_SCHEDULE_IMAGE_ID != #{event_schedule_image_id}

	USER_ID = #{user_id}

mapper.select:
SELECT
	*
FROM
	TABLE_A
WHERE
	(
	1 = 0

		EVENT_SCHEDULE_IMAGE_ID = #{event_schedule_image_id}

	) AND	
	USER_ID = #{user_id}

SELECT * FROM TABLE_A WHERE ( 1 = 0
EVENT_SCHEDULE_IMAGE_ID = #{event_schedule_image_id}
) AND USER_ID = #{user_id} AND SELF = 1

mapper.update:
UPDATE TABLE_A
SET
	SELF = 1
WHERE
	(
	1 = 0

		EVENT_SCHEDULE_IMAGE_ID = #{event_schedule_image_id}

	)
	AND
	USER_ID = #{user_id}
